$input = "bvwbjplbgvbhsrlpgdmjqwftvncz";

//: first marker after character 5
//nppdvjthqldpwncqszvftbrmjlhg: first marker after character 6
//nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg: first marker after character 10
//zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw'

process($input, 4);
process($input, 14);

function process($input, $uniqueLength) {
    for($i = 0; $i < strlen($input)-$uniqueLength; $i++) {
        $count = 0;
        for($j = 0; $j < $uniqueLength; $j++ ){
        //    echo "checking " . $input[$i+$j] . " "  . substr($input, $i, $uniqueLength) . PHP_EOL;
            $count += substr_count($input, $input[$i+$j], $i+$j, $uniqueLength-$j );
        }
        // echo $count . PHP_EOL;
        if ($count == $uniqueLength) {
            echo "Found it: " . $i + $uniqueLength . PHP_EOL;
            break;
        }
    }
}
